import React, { useState, useEffect } from 'react'; // Ensure React is imported
import AppointmentList from '../components/AppointmentList';
import '../styles/dashboard.css'; // Keep existing styles
import '../styles/main.css'; // Ensure main styles are included

function DoctorDashboard() {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // TODO: Replace with actual API call
    const fetchAppointments = async () => {
      try {
        // Mock data
        const mockAppointments = [
          { 
            id: 1, 
            patientName: 'John Doe', 
            date: '2025-04-10', 
            time: '10:00', 
            status: 'upcoming',
            reason: 'Annual checkup'
          },
          { 
            id: 2, 
            patientName: 'Jane Smith', 
            date: '2025-04-10', 
            time: '11:30', 
            status: 'upcoming',
            reason: 'Follow-up visit'
          },
        ];
        setAppointments(mockAppointments);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching appointments:', error);
        setLoading(false);
      }
    };

    fetchAppointments();
  }, []);

  const handleComplete = (appointmentId) => {
    // TODO: Replace with actual API call
    console.log('Completing appointment:', appointmentId);
    setAppointments(appointments.map(a => 
      a.id === appointmentId ? {...a, status: 'completed'} : a
    ));
  };

  if (loading) {
    return <div className="loading">Loading appointments...</div>;
  }

  return (
    <div className="dashboard-page"> {/* Use dashboard-page for overall padding/background */}
      <div className="dashboard-header">
        <h2>Doctor Dashboard</h2> {/* Use h2 for consistency */}
      </div>

      {/* Section for Appointments */}
      <div className="dashboard-section">
        <div className="dashboard-section-header">
          {/* Removed justify-content: space-between from header as there's no button */}
          <h3>Today's Appointments</h3>
          {/* No button needed here for doctors */}
        </div>
        <AppointmentList
          appointments={appointments}
          onComplete={handleComplete}
          userType="doctor"
        />
      </div>

      {/* Add more sections here if needed in the future */}
      {/* e.g., <div className="dashboard-section"><h3>Patient Records</h3>...</div> */}
    </div>
  );
}

export default DoctorDashboard;
