import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import LoginForm from '../components/LoginForm';
import '../styles/login.css';
import loginIllustration from '../assets/login-illustration.svg'; // Assuming you add an illustration

function LoginPage({ setIsAuthenticated }) { // Add setIsAuthenticated prop
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (credentials) => {
    try {
      // TODO: Replace with actual API call
      console.log('Login attempt with:', credentials);
      // TODO: Replace with actual API call
      console.log('Login attempt with:', credentials);
      // Mock successful login
      localStorage.setItem('token', 'mock-token'); // Replace with actual token from API
      localStorage.setItem('userType', credentials.userType);
      setIsAuthenticated(true); // Update auth state in App.js
      navigate(credentials.userType === 'doctor' ? '/doctor-dashboard' : '/patient-dashboard');
    } catch (err) {
      console.error("Login error:", err); // Log the actual error
      setError('Login failed. Please check your credentials.'); // More generic error
    }
  };

  return (
    <div className="auth-page login-page">
      <div className="auth-container">
        {/* Left Side - Illustration/Branding */}
        <div className="auth-visual">
          {/* You can use an image, illustration, or just a colored background */}
          {/* <img src={loginIllustration} alt="Login Illustration" /> */}
           <div className="visual-content">
             <h2>Clinic Appointments</h2>
             <p>Manage your health journey seamlessly.</p>
           </div>
        </div>

        {/* Right Side - Form */}
        <div className="auth-form-container">
          <div className="form-wrapper">
            <h2>Welcome Back</h2>
            <p className="subtitle">Login to access your dashboard.</p>
            {error && <div className="alert alert-danger">{error}</div>}
            <LoginForm onLogin={handleLogin} />
            <p className="auth-switch-link">
              Don't have an account? <a href="/register">Register here</a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
