import { useState } from 'react';
import { Link } from 'react-router-dom';
import { 
  FaUser, 
  FaSignInAlt, 
  FaUserPlus,
  FaCalendarAlt,
  FaUserMd,
  FaHome
} from 'react-icons/fa';
import '../styles/navbar.css';

function Navbar({ isAuthenticated }) {
  const userType = localStorage.getItem('userType');
  const token = localStorage.getItem('token');
  const [showDropdown, setShowDropdown] = useState(false);
  
  // Use both prop and localStorage check for reliability
  const isLoggedIn = isAuthenticated || !!token;

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userType');
    window.location.href = '/login';
  };

  return (
    <nav className="navbar">
      <div className="container">
        <Link to="/" className="navbar-brand">
          <FaHome className="icon" /> Clinic Appointment System
        </Link>
        
        <div className="navbar-links">
          {!isLoggedIn && (
            <>
              <Link to="/login" className="nav-link">
                Login
              </Link>
              <Link to="/register" className="nav-link">
                Register
              </Link>
            </>
          )}
          
          {isLoggedIn && (
            <>
              {userType === 'patient' && (
                <>
                  <Link to="/doctors" className="nav-link">
                    <FaUserMd className="icon" /> Doctors
                  </Link>
                  <Link to="/patient-dashboard" className="nav-link">
                    <FaCalendarAlt className="icon" /> Dashboard
                  </Link>
                </>
              )}
              {userType === 'doctor' && (
                <Link to="/doctor-dashboard" className="nav-link">
                  My Appointments
                </Link>
              )}

              <div className="profile-dropdown"
                onMouseEnter={() => setShowDropdown(true)}
                onMouseLeave={() => setShowDropdown(false)}
              >
                <button 
                  className="profile-btn"
                  onClick={() => setShowDropdown(!showDropdown)}
                >
                  Profile
                </button>
                
                {showDropdown && (
                  <div className="dropdown-menu" 
                    onMouseEnter={() => setShowDropdown(true)}
                    onMouseLeave={() => setShowDropdown(false)}
                  >
                    <Link to="/profile" className="dropdown-item">
                      View Profile
                    </Link>
                    <button 
                      onClick={handleLogout} 
                      className="dropdown-item logout-btn"
                    >
                      Logout
                    </button>
                  </div>
                )}
              </div>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
