import { useState } from 'react';
import { Link } from 'react-router-dom';
import { 
  FaUser, 
  FaSignInAlt, 
  FaUserPlus,
  FaCalendarAlt,
  FaUserMd,
  FaHome
} from 'react-icons/fa';
import '../styles/navbar.css';

function Navbar({ isAuthenticated }) {
  const userType = localStorage.getItem('userType');
  const [showDropdown, setShowDropdown] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userType');
    window.location.href = '/login';
  };

  return (
    <nav className="navbar">
      <div className="container">
        <Link to="/" className="navbar-brand">
          <FaHome className="icon" /> Clinic Appointment System
        </Link>
        
        <div className="navbar-links">
          {/* Links for Logged Out Users */}
          {!isAuthenticated && (
            <>
              <Link to="/login" className="nav-link">
                <FaSignInAlt className="icon" /> Login
              </Link>
              <Link to="/register" className="nav-link">
                <FaUserPlus className="icon" /> Register
              </Link>
            </>
          )}

          {/* Links for Logged In Users */}
          {isAuthenticated && (
            <>
              {/* Patient Links */}
              {userType === 'patient' && (
                <>
                  <Link to="/doctors" className="nav-link">
                    <FaUserMd className="icon" /> Doctors
                  </Link>
                  <Link to="/patient-dashboard" className="nav-link">
                    <FaCalendarAlt className="icon" /> Dashboard
                  </Link>
                </>
              )}

              {/* Doctor Links */}
              {userType === 'doctor' && (
                <>
                  <Link to="/doctor-dashboard" className="nav-link">
                     <FaCalendarAlt className="icon" /> Appointments {/* Added Icon */}
                  </Link>
                  {/* Add other doctor-specific links here if needed */}
                </>
              )}

              {/* Profile Dropdown (Common for logged-in users) */}
              <div className="profile-dropdown"
                onMouseEnter={() => setShowDropdown(true)}
                onMouseLeave={() => setShowDropdown(false)}
              >
                <button 
                  className="profile-btn"
                  onClick={() => setShowDropdown(!showDropdown)}
                >
                  <FaUser className="icon" />
                </button>
                
                {/* The dropdown menu's visibility is now controlled by CSS :hover on .profile-dropdown */}
                <div className="dropdown-menu">
                    <Link to="/profile" className="dropdown-item">
                      View Profile
                    </Link>
                    <button 
                      onClick={handleLogout} 
                      className="dropdown-item logout-btn"
                    >
                      Logout
                    </button>
                  </div>
                )}
              </div>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
