import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { FaUserMd } from 'react-icons/fa';
import AppointmentList from '../components/AppointmentList';
import AvailabilityModal from '../components/AvailabilityModal';
import EditProfileModal from '../components/EditProfileModal'; // Import the new modal
import '../styles/profile.css';
import '../styles/main.css';
import '../styles/availability.css';
import '../styles/modal.css'; // Ensure modal styles are loaded

// Mock Doctor data - Defined once
const mockDoctorData = {
  1: {
    id: 1,
    name: 'Dr. Evelyn Reed',
    specialization: 'Cardiology',
    email: 'evelyn.reed@appointix.test',
    phone: '555-123-4567',
    bio: 'Dr. Reed is a board-certified cardiologist with over 15 years of experience specializing in preventative care and heart health management.',
    profilePictureUrl: null
  },
  2: {
    id: 2,
    name: 'Dr. Marcus Chen',
    specialization: 'Neurology',
    email: 'marcus.chen@appointix.test',
    phone: '555-987-6543',
    bio: 'Specializing in neurological disorders, Dr. Chen is dedicated to providing comprehensive patient care and advancing research in neurology.',
    profilePictureUrl: null
  },
  3: {
    id: 3,
    name: 'Dr. Anya Sharma',
    specialization: 'Pediatrics',
    email: 'anya.sharma@appointix.test',
    phone: '555-111-2222',
    bio: 'Dr. Sharma provides compassionate pediatric care, focusing on child development and wellness from infancy through adolescence.',
    profilePictureUrl: null
  }
};

// Mock Availability Data (keyed by doctorId)
const defaultAvailability = {
  Monday:    { startTime: "09:00", endTime: "13:00", isAvailable: true },
  Tuesday:   { startTime: "09:00", endTime: "13:00", isAvailable: true },
  Wednesday: { startTime: "09:00", endTime: "13:00", isAvailable: true },
  Thursday:  { startTime: "09:00", endTime: "13:00", isAvailable: true },
  Friday:    { startTime: "09:00", endTime: "13:00", isAvailable: true },
  Saturday:  { startTime: "10:00", endTime: "12:00", isAvailable: false }, // Example different/off day
  Sunday:    { startTime: "",      endTime: "",      isAvailable: false },
};
// Add second time slot example if needed later
// Monday: { slots: [{ startTime: "09:00", endTime: "13:00" }, { startTime: "18:00", endTime: "21:00" }], isAvailable: true },


const mockDoctorAvailability = {
  1: { ...defaultAvailability }, // Dr. Reed uses default
  2: { // Dr. Chen has different hours
    ...defaultAvailability,
    Wednesday: { startTime: "14:00", endTime: "18:00", isAvailable: true },
    Friday:    { startTime: "", endTime: "", isAvailable: false },
  },
  3: { ...defaultAvailability }, // Dr. Sharma uses default
};


// Mock Appointment History Data - Defined once
const mockAppointmentHistory = {
  1: [ // Dr. Reed
    { id: 101, patientName: 'Alice Brown', date: '2025-04-01', time: '10:00', status: 'completed', reason: 'Checkup' },
    { id: 102, patientName: 'Bob White', date: '2025-03-20', time: '14:00', status: 'completed', reason: 'Consultation' },
  ],
  2: [ // Dr. Chen
    { id: 201, patientName: 'Charlie Green', date: '2025-04-05', time: '09:30', status: 'completed', reason: 'Follow-up' },
  ],
  3: [ // Dr. Sharma
    { id: 301, patientName: 'Diana Black', date: '2025-04-08', time: '11:00', status: 'completed', reason: 'Vaccination' },
    { id: 302, patientName: 'Ethan Gray', date: '2025-03-15', time: '15:00', status: 'completed', reason: 'Sick visit' },
  ]
};

function DoctorProfilePage() {
  const { id } = useParams();
  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [profilePicPreview, setProfilePicPreview] = useState(null);
  const [profilePicFile, setProfilePicFile] = useState(null);
  // Removed state for inline editing: isEditing, editablePhone, editableBio
  const [appointmentHistory, setAppointmentHistory] = useState([]);
  const [doctorAvailability, setDoctorAvailability] = useState(null);
  const [isAvailabilityModalOpen, setIsAvailabilityModalOpen] = useState(false);
  const [isEditProfileModalOpen, setIsEditProfileModalOpen] = useState(false); // State for profile edit modal

  useEffect(() => {
    // Simulate API calls
    const fetchData = () => {
      setLoading(true);
      setError('');
      const foundDoctor = mockDoctorData[id];
      if (foundDoctor) {
        setDoctor(foundDoctor);
        setEditablePhone(foundDoctor.phone || '');
        setEditableBio(foundDoctor.bio || '');
        // Simulate Availability Fetch
        const availability = mockDoctorAvailability[id] || { ...defaultAvailability }; // Fallback to default
        setDoctorAvailability(availability);
        // Simulate Appointment History Fetch
        const history = mockAppointmentHistory[id] || [];
        setAppointmentHistory(history);
      } else {
        setError('Doctor not found.');
      }
      setLoading(false);
    };

    const timer = setTimeout(fetchData, 300);
    return () => clearTimeout(timer);
  }, [id]);

  // --- Handlers ---
  const handleProfilePicChange = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('image/')) {
      setProfilePicFile(file);
      const reader = new FileReader();
      reader.onloadend = () => setProfilePicPreview(reader.result);
      reader.readAsDataURL(file);
    } else {
      setProfilePicFile(null);
      setProfilePicPreview(null);
    }
  };

  // --- Profile Edit Modal Handlers ---
  const handleOpenEditProfileModal = () => {
    // Reset picture preview when opening edit modal in case it was changed before cancelling
    setProfilePicPreview(null);
    setProfilePicFile(null);
    setIsEditProfileModalOpen(true);
  };

  const handleCloseEditProfileModal = () => {
    setIsEditProfileModalOpen(false);
  };

  const handleUpdateProfile = (updatedProfileData) => {
    // updatedProfileData contains { phone, bio } from the modal
    console.log("Updating profile data:", {
      ...updatedProfileData,
      pictureFile: profilePicFile ? profilePicFile.name : 'No new picture' // Keep picture handling separate for now
    });
    const updatedDoctor = {
      ...doctor,
      phone: updatedProfileData.phone, // Use data from modal
      bio: updatedProfileData.bio,     // Use data from modal
      // Picture update would likely be a separate API call or handled differently
      // For mock, we can keep the preview if one exists from the main page interaction
      profilePictureUrl: profilePicPreview || doctor.profilePictureUrl
    };
    setDoctor(updatedDoctor);
    // mockDoctorData[id] = updatedDoctor; // Mock persistence if needed

    // Close the modal and clear picture state
    handleCloseEditProfileModal();
    setProfilePicPreview(null); // Clear preview after saving profile text
    setProfilePicFile(null);
    // TODO: Add success/error message handling
  };
  // --- End Profile Edit Modal Handlers ---


  // --- Availability Modal Handlers ---
  const handleOpenAvailabilityModal = () => {
    setIsAvailabilityModalOpen(true);
  };

  const handleCloseAvailabilityModal = () => {
    setIsAvailabilityModalOpen(false);
  };

  const handleUpdateAvailability = (newAvailability) => {
    console.log("Updating availability to:", newAvailability);
    setDoctorAvailability(newAvailability); // Update state
    // mockDoctorAvailability[id] = newAvailability; // Mock persistence if needed
    handleCloseAvailabilityModal();
    // TODO: Add actual API call here later
  };

  // --- Render Logic ---
  if (loading) return <div className="loading">Loading doctor profile...</div>;
  if (error) return <div className="container page-container error">{error}</div>;
  if (!doctor) return <div className="container page-container">Doctor data unavailable.</div>;

  const displayImageUrl = profilePicPreview || doctor.profilePictureUrl;

  return (
    <div className="profile-page container page-container">
      <h1>Doctor Profile</h1>

      {/* Combined Details Section */}
      <div className="profile-section profile-header-section">
        <div className="profile-picture-container">
          {displayImageUrl ? (
            <img src={displayImageUrl} alt={`${doctor.name}'s profile`} className="profile-picture" />
          ) : (
            <div className="profile-picture-placeholder"><FaUserMd /></div>
          )}
          <label htmlFor="profilePicInput" className="btn btn-secondary btn-sm upload-btn">
            {displayImageUrl ? 'Change Picture' : 'Upload Picture'}
          </label>
          <input
            type="file" id="profilePicInput" accept="image/*"
            onChange={handleProfilePicChange} style={{ display: 'none' }}
          />
        </div>

        <div className="profile-header-details">
          {/* Basic Info */}
          <h2>{doctor.name}</h2>
          <p className="profile-specialization">{doctor.specialization}</p>
          <p className="profile-email">
            <a href={`mailto:${doctor.email}`}>{doctor.email}</a>
          </p>

          {/* Editable Details */}
          <div className="profile-details edit-section">
            <div className="detail-item">
              <label htmlFor={isEditing ? 'phone' : undefined}>Phone:</label>
              {isEditing ? (
                <input type="tel" id="phone" name="phone" className="form-control"
                  value={editablePhone} onChange={(e) => setEditablePhone(e.target.value)}
                  placeholder="Enter phone number" />
              ) : (
                <p>{doctor.phone || 'Not Provided'}</p>
              )}
            </div>
            <div className="detail-item">
              <label htmlFor={isEditing ? 'bio' : undefined}>Bio:</label>
              {isEditing ? (
                <textarea id="bio" name="bio" className="form-control" rows="4"
                  value={editableBio} onChange={(e) => setEditableBio(e.target.value)}
                  placeholder="Enter a short bio" />
              ) : (
                <p>{doctor.bio || 'Not Provided'}</p>
              )}
            </div>
          </div>

          {/* Action Buttons */}
          <div className="profile-actions">
            {!isEditing ? (
              <>
                <button onClick={handleEditToggle} className="btn btn-secondary btn-sm">Edit Profile</button>
                {/* Show Edit Availability only when NOT editing profile */}
                <button onClick={handleOpenAvailabilityModal} className="btn btn-secondary btn-sm">
                   Set Availability {/* Removed Icon */}
                </button>
              </>
            ) : (
              <div className="edit-actions"> {/* These show when editing profile */}
                <button onClick={handleSave} className="btn btn-primary btn-sm">Save Changes</button>
                <button onClick={handleEditToggle} className="btn btn-secondary btn-sm">Cancel</button>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Appointment History Section */}
      <div className="profile-section">
        <div className="profile-section-header">
          <h3>Appointment History</h3>
        </div>
        <AppointmentList
          appointments={appointmentHistory}
          userType="doctor"
          onComplete={() => {}}
          onCancel={() => {}}
        />
      </div>

      {/* Render AvailabilityModal */}
      <AvailabilityModal
        isOpen={isAvailabilityModalOpen}
        currentAvailability={doctorAvailability}
        onUpdate={handleUpdateAvailability}
        onClose={handleCloseAvailabilityModal}
      />
    </div>
  );
}

export default DoctorProfilePage;
