import React, { useState, useEffect } from 'react';
// import '../styles/modal.css'; // We'll create this later

function EditAppointmentModal({ isOpen, appointment, onUpdate, onClose }) {
  const [newDate, setNewDate] = useState('');
  const [newTime, setNewTime] = useState('');

  // Update local state when the appointment prop changes (when modal opens)
  useEffect(() => {
    if (appointment) {
      setNewDate(appointment.date);
      setNewTime(appointment.time);
    }
  }, [appointment]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!appointment) return; // Should not happen if modal is open

    // Basic validation (can be enhanced)
    if (!newDate || !newTime) {
      alert('Please select both date and time.');
      return;
    }

    onUpdate({
      id: appointment.id,
      date: newDate,
      time: newTime,
    });
  };

  if (!isOpen || !appointment) {
    return null; // Don't render anything if modal is closed or no appointment data
  }

  return (
    <div className="modal-backdrop"> {/* Use backdrop class */}
      <div className="modal-container"> {/* Use container class */}
        <div className="modal-header">
          <h2>Reschedule Appointment</h2>
          <button onClick={onClose} className="modal-close-btn">&times;</button>
        </div>
        <div className="modal-body">
          <p><strong>Doctor:</strong> {appointment.doctorName}</p>
          <p><strong>Reason:</strong> {appointment.reason}</p>
          <hr />
          <form onSubmit={handleSubmit} className="edit-appointment-form">
            <div className="form-group">
              <label htmlFor="edit-date">New Date:</label>
              <input
                type="date"
                id="edit-date"
                className="form-control"
                value={newDate}
                onChange={(e) => setNewDate(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="edit-time">New Time:</label>
              <input
                type="time"
                id="edit-time"
                className="form-control"
                value={newTime}
                onChange={(e) => setNewTime(e.target.value)}
                required
              />
            </div>
            <div className="modal-footer">
              <button type="submit" className="btn btn-primary">
                Save Changes
              </button>
              <button type="button" onClick={onClose} className="btn btn-secondary">
                Cancel
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default EditAppointmentModal;
