import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import AppointmentForm from '../components/AppointmentForm';
import { isTimeSlotAvailable } from '../utils/availabilityUtils'; // Import validation function
import '../styles/appointment.css';
import '../styles/main.css';

// Mock Availability Data (copied from DoctorProfilePage for now)
// TODO: Refactor mock data into a shared location
const defaultAvailability = {
  Monday:    { startTime: "09:00", endTime: "13:00", isAvailable: true },
  Tuesday:   { startTime: "09:00", endTime: "13:00", isAvailable: true },
  Wednesday: { startTime: "09:00", endTime: "13:00", isAvailable: true },
  Thursday:  { startTime: "09:00", endTime: "13:00", isAvailable: true },
  Friday:    { startTime: "09:00", endTime: "13:00", isAvailable: true },
  Saturday:  { startTime: "10:00", endTime: "12:00", isAvailable: false },
  Sunday:    { startTime: "",      endTime: "",      isAvailable: false },
};
const mockDoctorAvailability = {
  1: { ...defaultAvailability },
  2: {
    ...defaultAvailability,
    Wednesday: { startTime: "14:00", endTime: "18:00", isAvailable: true },
    Friday:    { startTime: "", endTime: "", isAvailable: false },
  },
  3: { ...defaultAvailability },
};

// Mock Doctor Data (needed for availability lookup)
// TODO: Refactor mock data into a shared location
const mockDoctors = [
  { id: 1, name: 'Dr. Smith', specialization: 'Cardiology' },
  { id: 2, name: 'Dr. Johnson', specialization: 'Neurology' },
  { id: 3, name: 'Dr. Williams', specialization: 'Pediatrics' },
];


function BookAppointmentPage() {
  const { doctorId } = useParams();
  const navigate = useNavigate();
  const [doctor, setDoctor] = useState(null);
  const [doctorAvailability, setDoctorAvailability] = useState(null); // State for availability
  const [loading, setLoading] = useState(true);
  const [bookingError, setBookingError] = useState(''); // State for booking errors

  useEffect(() => {
    // TODO: Replace with actual API call
    const fetchDoctor = async () => {
      try {
        // Mock data
        const mockDoctors = [
          { id: 1, name: 'Dr. Smith', specialization: 'Cardiology' },
          { id: 2, name: 'Dr. Johnson', specialization: 'Neurology' },
          { id: 3, name: 'Dr. Williams', specialization: 'Pediatrics' },
        ];
        const foundDoctor = mockDoctors.find(d => d.id === parseInt(doctorId));
        setDoctor(foundDoctor);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching doctor:', error);
        setLoading(false);
      }
    };

    fetchDoctor();
  }, [doctorId]);

  const handleSubmit = async (appointmentData) => {
    try {
      // TODO: Replace with actual API call
      console.log('Booking appointment with:', appointmentData);
      // Mock successful booking
      navigate('/patient-dashboard');
    } catch (error) {
      console.error('Error booking appointment:', error);
    }
  };

  if (loading) {
    return <div className="loading">Loading doctor details...</div>;
  }

  if (!doctor) {
    return <div className="error">Doctor not found</div>;
  }

  return (
    // Use the main container class from appointment.css
    <div className="book-appointment-page">
      <div className="appointment-form-container"> {/* Wrap form in the styled container */}
        <div className="book-appointment-header">
          <h2>Book Appointment</h2> {/* Use h2 for consistency */}
          <p className="doctor-info">
            with Dr. {doctor.name} ({doctor.specialization})
          </p>
        </div>
        <AppointmentForm doctorId={doctorId} onSubmit={handleSubmit} />
      </div>
    </div>
  );
}

export default BookAppointmentPage;
