import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import AppointmentList from '../components/AppointmentList';
import EditAppointmentModal from '../components/EditAppointmentModal'; // Import the modal
import '../styles/dashboard.css';
import '../styles/main.css';
import '../styles/modal.css'; // Import modal styles

function PatientDashboard() {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [appointmentToEdit, setAppointmentToEdit] = useState(null);

  useEffect(() => {
    // TODO: Replace with actual API call
    const fetchAppointments = async () => {
      try {
        // Mock data
        const mockAppointments = [
          { 
            id: 1,
            doctorId: 1, // Added doctorId
            doctorName: 'Dr. Smith',
            date: '2025-04-10',
            time: '10:00',
            status: 'upcoming',
            reason: 'Annual checkup'
          },
          {
            id: 2,
            doctorId: 2, // Added doctorId
            doctorName: 'Dr. Johnson',
            date: '2025-03-15',
            time: '14:30',
            status: 'completed',
            reason: 'Headache consultation'
          },
          // Add another upcoming for testing edit
          {
            id: 3,
            doctorId: 2, // Dr. Johnson again
            doctorName: 'Dr. Johnson',
            date: '2025-04-15',
            time: '16:00',
            status: 'upcoming',
            reason: 'Follow-up'
          }
        ];
        setAppointments(mockAppointments);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching appointments:', error);
        setLoading(false);
      }
    };

    fetchAppointments();
  }, []);

  const handleCancel = (appointmentId) => {
    // TODO: Replace with actual API call
    console.log('Canceling appointment:', appointmentId);
    setAppointments(appointments.filter(a => a.id !== appointmentId));
  };

  // --- Edit Modal Handlers ---
  const handleEditRequest = (appointment) => {
    console.log("Editing appointment:", appointment);
    setAppointmentToEdit(appointment);
    setIsEditModalOpen(true);
  };

  const handleCloseEditModal = () => {
    setIsEditModalOpen(false);
    setAppointmentToEdit(null);
  };

  const handleUpdateAppointment = (updatedData) => {
    // updatedData should contain { id, date, time }
    console.log("Updating appointment with:", updatedData);
    setAppointments(prevAppointments =>
      prevAppointments.map(app =>
        app.id === updatedData.id
          ? { ...app, date: updatedData.date, time: updatedData.time }
          : app
      )
    );
    handleCloseEditModal();
    // TODO: Add actual API call here later
  };
  // --- End Edit Modal Handlers ---


  if (loading) {
    return <div className="loading">Loading appointments...</div>;
  }

  return (
    <div className="dashboard-page"> {/* Use dashboard-page for overall padding/background */}
      <div className="dashboard-header">
        <h2>My Dashboard</h2> {/* Use h2 for consistency */}
      </div>

      {/* Section for Appointments */}
      <div className="dashboard-section">
        <div className="dashboard-section-header">
          <h3>Upcoming & Past Appointments</h3>
          <Link to="/doctors" className="btn btn-primary"> {/* Use consistent button style */}
            Book New Appointment
          </Link>
        </div>
        <AppointmentList
          appointments={appointments}
          onCancel={handleCancel}
          onEditRequest={handleEditRequest} // Pass the handler
          userType="patient"
        />
      </div>

      {/* TODO: Add EditAppointmentModal component here later */}
      {/* <EditAppointmentModal
        isOpen={isEditModalOpen}
        appointment={appointmentToEdit}
        onUpdate={handleUpdateAppointment}
        onClose={handleCloseEditModal}
      />

      {/* Render the Edit Modal */}
      <EditAppointmentModal
        isOpen={isEditModalOpen}
        appointment={appointmentToEdit}
        onUpdate={handleUpdateAppointment}
        onClose={handleCloseEditModal}
      />

    </div>
  );
}

export default PatientDashboard;
