function AppointmentList({ appointments, onCancel, onComplete, userType }) {
  const getStatusBadge = (status) => {
    const statusClasses = {
      upcoming: 'badge-primary',
      completed: 'badge-success',
      cancelled: 'badge-danger'
    };
    return (
      <span className={`badge ${statusClasses[status] || ''}`}>
        {status}
      </span>
    );
  };

  return (
    <div className="appointment-list">
      {appointments.length === 0 ? (
        <p>No appointments found</p>
      ) : (
        <ul>
          {appointments.map(appointment => (
            <li key={appointment.id} className="appointment-item">
              <div className="appointment-header">
                <h3>
                  {userType === 'patient' 
                    ? appointment.doctorName 
                    : appointment.patientName}
                </h3>
                {getStatusBadge(appointment.status)}
              </div>
              <div className="appointment-details">
                <p>
                  <strong>Date:</strong> {appointment.date} at {appointment.time}
                </p>
                <p>
                  <strong>Reason:</strong> {appointment.reason}
                </p>
              </div>
              {userType === 'patient' && appointment.status === 'upcoming' && (
                <button 
                  onClick={() => onCancel(appointment.id)}
                  className="btn btn-danger"
                >
                  Cancel
                </button>
              )}
              {userType === 'doctor' && appointment.status === 'upcoming' && (
                <button 
                  onClick={() => onComplete(appointment.id)}
                  className="btn btn-success"
                >
                  Mark Complete
                </button>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default AppointmentList;
