import React from 'react';
import { Link } from 'react-router-dom';

// Assume doctor object has { id, name, specialization, bio (optional) }
function DoctorCard({ doctor }) {
  return (
    <div className="doctor-card">
      {/* Image Placeholder */}
      <div className="doctor-card-image-placeholder">
        {/* Content inside placeholder, e.g., initials or icon */}
        {/* Example: <span>{doctor.name.substring(0, 1)}</span> */}
      </div>

      {/* Card Content */}
      <div className="doctor-card-content">
        {/* Header */}
        <div className="doctor-card-header">
          <h3 className="doctor-name">{doctor.name || 'Doctor Name'}</h3>
          <p className="doctor-specialty">{doctor.specialization || 'Specialization'}</p>
        </div>

        {/* Body (Optional) */}
        <div className="doctor-card-body">
          {doctor.bio && <p>{doctor.bio}</p>}
          {!doctor.bio && <p>Experienced medical professional dedicated to patient care.</p>} {/* Default text */}
        </div>
      </div>

      {/* Footer with Action Button */}
      <div className="doctor-card-footer">
        <Link to={`/book/${doctor.id}`} className="btn btn-primary"> {/* Added btn-primary */}
          Book Appointment
        </Link>
      </div>
    </div>
  );
}

export default DoctorCard;
