import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { FaUserMd } from 'react-icons/fa';
import AppointmentList from '../components/AppointmentList'; // Import AppointmentList
import '../styles/profile.css';
import '../styles/main.css';

// Mock Doctor data - Defined once
const mockDoctorData = {
  1: {
    id: 1,
    name: 'Dr. Evelyn Reed',
    specialization: 'Cardiology',
    email: 'evelyn.reed@appointix.test',
    phone: '555-123-4567',
    bio: 'Dr. Reed is a board-certified cardiologist with over 15 years of experience specializing in preventative care and heart health management.',
    profilePictureUrl: null
  },
  2: {
    id: 2,
    name: 'Dr. Marcus Chen',
    specialization: 'Neurology',
    email: 'marcus.chen@appointix.test',
    phone: '555-987-6543',
    bio: 'Specializing in neurological disorders, Dr. Chen is dedicated to providing comprehensive patient care and advancing research in neurology.',
    profilePictureUrl: null
  },
  3: {
    id: 3,
    name: 'Dr. Anya Sharma',
    specialization: 'Pediatrics',
    email: 'anya.sharma@appointix.test',
    phone: '555-111-2222',
    bio: 'Dr. Sharma provides compassionate pediatric care, focusing on child development and wellness from infancy through adolescence.',
    profilePictureUrl: null
  }
};

// Mock Appointment History Data - Defined once
const mockAppointmentHistory = {
  1: [ // Dr. Reed
    { id: 101, patientName: 'Alice Brown', date: '2025-04-01', time: '10:00', status: 'completed', reason: 'Checkup' },
    { id: 102, patientName: 'Bob White', date: '2025-03-20', time: '14:00', status: 'completed', reason: 'Consultation' },
  ],
  2: [ // Dr. Chen
    { id: 201, patientName: 'Charlie Green', date: '2025-04-05', time: '09:30', status: 'completed', reason: 'Follow-up' },
  ],
  3: [ // Dr. Sharma
    { id: 301, patientName: 'Diana Black', date: '2025-04-08', time: '11:00', status: 'completed', reason: 'Vaccination' },
    { id: 302, patientName: 'Ethan Gray', date: '2025-03-15', time: '15:00', status: 'completed', reason: 'Sick visit' },
  ]
};

function DoctorProfilePage() {
  const { id } = useParams();
  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [profilePicPreview, setProfilePicPreview] = useState(null);
  const [profilePicFile, setProfilePicFile] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editablePhone, setEditablePhone] = useState('');
  const [editableBio, setEditableBio] = useState('');
  const [appointmentHistory, setAppointmentHistory] = useState([]);

  useEffect(() => {
    // Simulate API calls
    const fetchData = () => {
      setLoading(true);
      setError('');
      const foundDoctor = mockDoctorData[id];
      if (foundDoctor) {
        setDoctor(foundDoctor);
        setEditablePhone(foundDoctor.phone || '');
        setEditableBio(foundDoctor.bio || '');
        const history = mockAppointmentHistory[id] || [];
        setAppointmentHistory(history);
      } else {
        setError('Doctor not found.');
      }
      setLoading(false);
    };

    const timer = setTimeout(fetchData, 300); // Shorter delay
    return () => clearTimeout(timer);
  }, [id]);

  // --- Handlers ---
  const handleProfilePicChange = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('image/')) {
      setProfilePicFile(file);
      const reader = new FileReader();
      reader.onloadend = () => setProfilePicPreview(reader.result);
      reader.readAsDataURL(file);
    } else {
      setProfilePicFile(null);
      setProfilePicPreview(null);
    }
  };

  const handleEditToggle = () => {
    if (!isEditing) {
      setEditablePhone(doctor.phone || '');
      setEditableBio(doctor.bio || '');
    } else {
      setProfilePicPreview(null);
      setProfilePicFile(null);
    }
    setIsEditing(!isEditing);
  };

  const handleSave = () => {
    console.log("Saving profile data:", {
      phone: editablePhone,
      bio: editableBio,
      pictureFile: profilePicFile ? profilePicFile.name : 'No new picture'
    });
    const updatedDoctor = {
      ...doctor,
      phone: editablePhone,
      bio: editableBio,
      profilePictureUrl: profilePicPreview || doctor.profilePictureUrl
    };
    setDoctor(updatedDoctor);
    // mockDoctorData[id] = updatedDoctor; // Mock persistence if needed
    setIsEditing(false);
    setProfilePicPreview(null);
    setProfilePicFile(null);
    // TODO: Add success/error message handling
  };

  // --- Render Logic ---
  if (loading) return <div className="loading">Loading doctor profile...</div>;
  if (error) return <div className="container page-container error">{error}</div>;
  if (!doctor) return <div className="container page-container">Doctor data unavailable.</div>;

  const displayImageUrl = profilePicPreview || doctor.profilePictureUrl;

  return (
    <div className="profile-page container page-container">
      <h1>Doctor Profile</h1>

      {/* Combined Details Section */}
      <div className="profile-section profile-header-section">
        <div className="profile-picture-container">
          {displayImageUrl ? (
            <img src={displayImageUrl} alt={`${doctor.name}'s profile`} className="profile-picture" />
          ) : (
            <div className="profile-picture-placeholder"><FaUserMd /></div>
          )}
          <label htmlFor="profilePicInput" className="btn btn-secondary btn-sm upload-btn">
            {displayImageUrl ? 'Change Picture' : 'Upload Picture'}
          </label>
          <input
            type="file" id="profilePicInput" accept="image/*"
            onChange={handleProfilePicChange} style={{ display: 'none' }}
          />
        </div>

        <div className="profile-header-details">
          {/* Basic Info */}
          <h2>{doctor.name}</h2>
          <p className="profile-specialization">{doctor.specialization}</p>
          <p className="profile-email">
            <a href={`mailto:${doctor.email}`}>{doctor.email}</a>
          </p>

          {/* Editable Details */}
          <div className="profile-details edit-section">
            <div className="detail-item">
              <label htmlFor={isEditing ? 'phone' : undefined}>Phone:</label>
              {isEditing ? (
                <input type="tel" id="phone" name="phone" className="form-control"
                  value={editablePhone} onChange={(e) => setEditablePhone(e.target.value)}
                  placeholder="Enter phone number" />
              ) : (
                <p>{doctor.phone || 'Not Provided'}</p>
              )}
            </div>
            <div className="detail-item">
              <label htmlFor={isEditing ? 'bio' : undefined}>Bio:</label>
              {isEditing ? (
                <textarea id="bio" name="bio" className="form-control" rows="4"
                  value={editableBio} onChange={(e) => setEditableBio(e.target.value)}
                  placeholder="Enter a short bio" />
              ) : (
                <p>{doctor.bio || 'Not Provided'}</p>
              )}
            </div>
          </div>

          {/* Action Buttons */}
          <div className="profile-actions">
            {!isEditing ? (
              <button onClick={handleEditToggle} className="btn btn-secondary btn-sm">Edit Profile</button>
            ) : (
              <div className="edit-actions">
                <button onClick={handleSave} className="btn btn-primary btn-sm">Save Changes</button>
                <button onClick={handleEditToggle} className="btn btn-secondary btn-sm">Cancel</button>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Appointment History Section */}
      <div className="profile-section">
        <div className="profile-section-header">
          <h3>Appointment History</h3>
        </div>
        <AppointmentList
          appointments={appointmentHistory}
          userType="doctor"
          onComplete={() => {}} // Placeholder
          onCancel={() => {}}   // Placeholder
        />
      </div>
    </div>
  );
}

export default DoctorProfilePage;
