import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/profile.css'; // Import the profile styles
import '../styles/main.css'; // For base styles

// Mock data - replace with API call later
const mockDoctorData = {
  1: {
    id: 1,
    name: 'Dr. Evelyn Reed',
    specialization: 'Cardiology',
    email: 'evelyn.reed@appointix.test',
    phone: '555-123-4567',
    bio: 'Dr. Reed is a board-certified cardiologist with over 15 years of experience specializing in preventative care and heart health management.',
    profilePictureUrl: null // Or path to a default image if available
  },
  2: {
    id: 2,
    name: 'Dr. Marcus Chen',
    specialization: 'Neurology',
    email: 'marcus.chen@appointix.test',
    phone: '555-987-6543',
    bio: 'Specializing in neurological disorders, Dr. Chen is dedicated to providing comprehensive patient care and advancing research in neurology.',
    profilePictureUrl: null
  },
  // Add more mock doctors as needed
  3: {
    id: 3,
    name: 'Dr. Anya Sharma',
    specialization: 'Pediatrics',
    email: 'anya.sharma@appointix.test',
    phone: '555-111-2222',
    bio: 'Dr. Sharma provides compassionate pediatric care, focusing on child development and wellness from infancy through adolescence.',
    profilePictureUrl: null
  }
};

import { FaUserMd } from 'react-icons/fa'; // Icon for placeholder

function DoctorProfilePage() {
  const { id } = useParams();
  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [profilePicPreview, setProfilePicPreview] = useState(null);
  const [profilePicFile, setProfilePicFile] = useState(null);
  const [isEditing, setIsEditing] = useState(false); // State for edit mode
  const [editablePhone, setEditablePhone] = useState('');
  const [editableBio, setEditableBio] = useState('');

  useEffect(() => {
    setLoading(true);
    setError('');
    // Simulate API call
    setTimeout(() => {
      const foundDoctor = mockDoctorData[id];
      if (foundDoctor) {
        setDoctor(foundDoctor);
        // Initialize editable fields when doctor data loads
        setEditablePhone(foundDoctor.phone || '');
        setEditableBio(foundDoctor.bio || '');
      } else {
        setError('Doctor not found.');
      }
      setLoading(false);
    }, 500); // Simulate network delay
  }, [id]); // Re-run if ID changes

  if (loading) {
    return <div className="loading">Loading doctor profile...</div>;
  }

  if (error) {
    // You might want a more styled error page/component
    return <div className="container page-container error">{error}</div>;
  }

  if (!doctor) {
    // Should ideally not happen if error is handled, but good fallback
    return <div className="container page-container">Doctor data unavailable.</div>;
  }

  // --- Handlers ---
  const handleProfilePicChange = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('image/')) {
      setProfilePicFile(file); // Store file for potential upload
      const reader = new FileReader();
      reader.onloadend = () => {
        setProfilePicPreview(reader.result); // Set preview URL
      };
      reader.readAsDataURL(file);
    } else {
      // Handle invalid file type if needed
      setProfilePicFile(null);
      setProfilePicPreview(null);
    }
  };

  const handleEditToggle = () => {
    if (!isEditing) {
      // Entering edit mode, ensure editable fields match current doctor state
      setEditablePhone(doctor.phone || '');
      setEditableBio(doctor.bio || '');
    } else {
      // Cancelling edit mode, reset preview if any
      setProfilePicPreview(null);
      setProfilePicFile(null);
    }
    setIsEditing(!isEditing);
  };

  const handleSave = () => {
    // Simulate saving data
    console.log("Saving profile data:", {
      phone: editablePhone,
      bio: editableBio,
      pictureFile: profilePicFile ? profilePicFile.name : 'No new picture'
    });

    // Update the main doctor state (mock update)
    const updatedDoctor = {
      ...doctor,
      phone: editablePhone,
      bio: editableBio,
      // If there's a preview, make it the new "official" URL for mock display
      profilePictureUrl: profilePicPreview || doctor.profilePictureUrl
    };
    setDoctor(updatedDoctor);

    // Mock data persistence (if needed for demo) - This wouldn't be done in a real app
    // mockDoctorData[id] = updatedDoctor;

    // Exit edit mode and clear preview/file state
    setIsEditing(false);
    setProfilePicPreview(null);
    setProfilePicFile(null);

    // TODO: Add success/error message handling based on actual API response
  };


  // --- Render Logic ---
  const displayImageUrl = profilePicPreview || doctor.profilePictureUrl;

  return (
    <div className="profile-page container page-container"> {/* Basic structure */}
      <h1>Doctor Profile</h1>
      <div className="profile-section profile-header-section"> {/* Section for Pic + Basic Info */}
        <div className="profile-picture-container">
          {displayImageUrl ? (
            <img src={displayImageUrl} alt={`${doctor.name}'s profile`} className="profile-picture" />
          ) : (
            <div className="profile-picture-placeholder">
              <FaUserMd /> {/* Placeholder Icon */}
            </div>
          )}
          <label htmlFor="profilePicInput" className="btn btn-secondary btn-sm upload-btn">
            {displayImageUrl ? 'Change Picture' : 'Upload Picture'}
          </label>
          <input
            type="file"
            id="profilePicInput"
            accept="image/*"
            onChange={handleProfilePicChange}
            style={{ display: 'none' }} // Hide the default input
          />
        </div>
        <div className="profile-header-details">
          <h2>{doctor.name}</h2>
          <p className="profile-specialization">{doctor.specialization}</p>
          <p className="profile-email">
             <a href={`mailto:${doctor.email}`}>{doctor.email}</a>
          </p>
          {/* Add other non-editable info here if needed */}
        </div>
      </div>

      {/* Contact & Bio Section */}
      <div className="profile-section">
        <div className="profile-section-header">
          <h3>Contact & Bio</h3>
          {!isEditing ? (
            <button onClick={handleEditToggle} className="btn btn-secondary btn-sm">
              Edit Profile
            </button>
          ) : (
            <div className="edit-actions">
              <button onClick={handleSave} className="btn btn-primary btn-sm">
                Save Changes
              </button>
              <button onClick={handleEditToggle} className="btn btn-secondary btn-sm">
                Cancel
              </button>
            </div>
          )}
        </div>

        <div className="profile-details">
          <div className="detail-item">
            <label htmlFor="phone">Phone:</label>
            {isEditing ? (
              <input
                type="tel"
                id="phone"
                name="phone"
                className="form-control"
                value={editablePhone}
                onChange={(e) => setEditablePhone(e.target.value)}
                placeholder="Enter phone number"
              />
            ) : (
              <p>{doctor.phone || 'Not Provided'}</p>
            )}
          </div>

          <div className="detail-item">
            <label htmlFor="bio">Bio:</label>
            {isEditing ? (
              <textarea
                id="bio"
                name="bio"
                className="form-control"
                rows="5"
                value={editableBio}
                onChange={(e) => setEditableBio(e.target.value)}
                placeholder="Enter a short bio"
              />
            ) : (
              <p>{doctor.bio || 'Not Provided'}</p>
            )}
          </div>
        </div>
      </div>
      {/* TODO: Implement Step 8 (Styling) */}
    </div>
  );
}

export default DoctorProfilePage;
