import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import LoginForm from '../components/LoginForm';
import '../styles/login.css';
// import loginIllustration from '../assets/login-illustration.svg'; // Removed unused import

function LoginPage({ setIsAuthenticated }) { // Add setIsAuthenticated prop
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (credentials) => {
    setError(''); // Clear previous errors
    try {
      // --- Actual API Call ---
      const response = await fetch('/api/login', { // Replace with your actual backend endpoint
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(credentials),
      });

      const data = await response.json();

      if (!response.ok) {
        // Handle backend errors (e.g., wrong password, user not found)
        throw new Error(data.message || `HTTP error! status: ${response.status}`);
      }

      // --- Login Successful ---
      console.log('Login successful:', data);
      localStorage.setItem('token', data.token); // Store the token from the backend response
      localStorage.setItem('userType', credentials.userType); // Or get userType from backend response if available
      setIsAuthenticated(true); // Update auth state in App.js
      navigate(credentials.userType === 'doctor' ? '/doctor-dashboard' : '/patient-dashboard');

    } catch (err) {
      console.error("Login error:", err);
      // Set error message based on the caught error, or a generic one
      setError(err.message || 'Login failed. Please check your credentials or server connection.');
    }
  };

  return (
    <div className="auth-page login-page">
      <div className="auth-container">
        {/* Left Side - Illustration/Branding */}
        <div className="auth-visual">
          {/* You can use an image, illustration, or just a colored background */}
          {/* <img src={loginIllustration} alt="Login Illustration" /> */}
           <div className="visual-content">
             <h2>Clinic Appointments</h2>
             <p>Manage your health journey seamlessly.</p>
           </div>
        </div>

        {/* Right Side - Form */}
        <div className="auth-form-container">
          <div className="form-wrapper">
            <h2>Welcome Back</h2>
            <p className="subtitle">Login to access your dashboard.</p>
            {error && <div className="alert alert-danger">{error}</div>}
            <LoginForm onLogin={handleLogin} />
            <p className="auth-switch-link">
              Don't have an account? <a href="/register">Register here</a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
